class Solution:
    # @param {string} s
    # @return {string}
    def longestPalindrome(self, s):
        if len(s) <= 1:
            return s
        longest_palindrome = s[0]

        str_len = len(s)
        for i in range(str_len):
            for half_len in range(len(longest_palindrome) / 2, str_len / 2 + 1):
                if i + half_len + 1 > str_len or i - half_len < 0:
                    break
                if s[i-half_len:i] == s[i+1:i+1+half_len][::-1]:
                    if half_len * 2 + 1 > len(longest_palindrome):
                        longest_palindrome = s[i-half_len:i+half_len+1]
                else:
                    break

            for half_len in range(len(longest_palindrome) / 2 + 1, str_len / 2 + 1):
                if i + half_len > str_len or i - half_len < 0:
                    break
                if s[i-half_len:i] == s[i:i+half_len][::-1]:
                    if half_len * 2 > len(longest_palindrome):
                        longest_palindrome = s[i-half_len:i+half_len]
                else:
                    break
        return longest_palindrome
                
